package javaExercise;

public class Opdracht6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 125;
		int num2 = 24;
        int result = num + num2;
		int result2 = num - num2;
		int result3 = num * num2;
		int result4 = num / num2;
		int result5 = num % num2;
		
		System.out.println(num+"+"+num2+"="+result);
		System.out.println(num+"-"+num2+"="+result2);
		System.out.println(num+"x"+num2+"="+result3);
		System.out.println(num+"/"+num2+"="+result4);
		System.out.println(num+"nom"+num2+"="+result5);
	}

}
